import React, { useState, useMemo } from "react";
import * as XLSX from "xlsx";
import Papa from "papaparse";
import { Upload, Download, BarChart3, FileSpreadsheet, TrendingUp, Users, UserCheck,  Bell, User, Settings, MessageSquare, Tag, Hash, BrainCircuit, Zap, X, Briefcase, Home, Filter, Calendar, Building2, Award, Target, CheckCircle2, AlertCircle, ArrowRight, Activity, Sparkles, Eye, Search } from "lucide-react";


// ✨ [수정] onProgress 콜백을 받도록 함수 시그니처 변경
const runAnalysisForRowByRow = async (data, textColumn, mode, analysisType, onProgress) => {
  if (!data || data.length === 0 || !textColumn) {
    if (onProgress) onProgress(1, 1); // 데이터가 없어도 완료로 처리
    return {};
  }
  const textsToProcess = data
    .map((row) => ({ id: row.uniqueId, text: row[textColumn] }))
    .filter(item => item.text && typeof item.text === 'string' && item.text.trim().length >= 5);

  if (textsToProcess.length === 0) {
    console.log(`'${textColumn}'에 대한 키워드 추출용 텍스트 데이터가 충분하지 않습니다.`);
    if (onProgress) onProgress(1, 1); // 처리할 텍스트가 없어도 완료로 처리
    return {};
  }

  const endpointMap = {
    llm: '/extract-keywords-llm',
    pos: '/extract-keywords-pos',
    textrank: '/extract-keywords-textrank'
  };
  const endpoint = endpointMap[mode] || endpointMap.llm;

  const allKeywordData = {};
  let processedCount = 0;
  const totalCount = textsToProcess.length;

  for (const item of textsToProcess) {
    const bodyPayload = { text: item.text };
    try {
      const response = await fetch(`http://127.0.0.1:8000${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bodyPayload),
      });
      if (response.ok) {
        allKeywordData[item.id] = await response.json();
      } else {
        allKeywordData[item.id] = [];
      }
    } catch (error) {
      allKeywordData[item.id] = [];
    }
    processedCount++;
    // ✨ [수정] 각 항목 처리 후 진행률 콜백 호출
    if (onProgress) {
      onProgress(processedCount, totalCount);
    }
  }
  return allKeywordData;
};

 const FileUploader = ({ onFileUpload }) => { 
     const [isDragging, setIsDragging] = useState(false); 
     const handleDragEnter = (e) => { e.preventDefault(); setIsDragging(true); }; 
     const handleDragLeave = (e) => { e.preventDefault(); setIsDragging(false); }; 
     const handleDragOver = (e) => { e.preventDefault(); }; 
     const handleDrop = (e) => { 
       e.preventDefault(); 
       setIsDragging(false); 
       const files = e.dataTransfer.files; 
       if (files.length > 0) onFileUpload(files[0]); 
     }; 
     const handleFileSelect = (e) => { 
       const file = e.target.files[0]; 
       if (file) onFileUpload(file); 
     }; 

     return ( 
       <div className="w-full max-w-2xl mx-auto"> 
         <div className="text-center mb-8"> 
           <h2 className="text-2xl font-bold text-gray-900 mb-2">면담 데이터 분석 시작</h2> 
