import React, { useState, useEffect, useMemo } from "react";
import * as XLSX from "xlsx";
import Papa from "papaparse";
import { Upload, Download, BarChart3, FileSpreadsheet, TrendingUp, Users, UserCheck, Clock, Bell, User, Settings, MessageSquare, Tag, Hash, X, Briefcase, Home } from "lucide-react";

// --- 한 줄(row)마다 키워드 추출 API를 호출하는 함수 ---
const runAnalysisForRowByRow = async (data, textColumn) => {
  if (!data || data.length === 0 || !textColumn) return {};

  const textsToProcess = data
    .map((row) => ({ id: row.uniqueId, text: row[textColumn] }))
    .filter(item => item.text && typeof item.text === 'string' && item.text.trim().length >= 5);

  if (textsToProcess.length === 0) {
    console.log(`'${textColumn}'에 대한 키워드 추출용 텍스트 데이터가 충분하지 않습니다.`);
    return {};
  }

  const allKeywordData = {};

  await Promise.all(textsToProcess.map(async (item) => {
    try {
      const response = await fetch(`http://127.0.0.1:8000/extract-keywords`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: item.text }),
      });

      if (response.ok) {
        allKeywordData[item.id] = await response.json();
      } else {
        allKeywordData[item.id] = [];
      }
    } catch (error) {
      allKeywordData[item.id] = [];
    }
  }));

  return allKeywordData;
};

// --- 파일 업로더 컴포넌트 ---
const FileUploader = ({ onFileUpload, loading, loadingMessage }) => {
    const [isDragging, setIsDragging] = useState(false);
    const handleDragEnter = (e) => { e.preventDefault(); setIsDragging(true); };
    const handleDragLeave = (e) => { e.preventDefault(); setIsDragging(false); };
    const handleDragOver = (e) => { e.preventDefault(); };
    const handleDrop = (e) => {
      e.preventDefault();
      setIsDragging(false);
      const files = e.dataTransfer.files;
      if (files.length > 0) onFileUpload(files[0]);
    };
    const handleFileSelect = (e) => {
      const file = e.target.files[0];
      if (file) onFileUpload(file);
    };
    return (
      <div className="max-w-lg mx-auto">
        <div
          className={`relative border-2 border-dashed rounded-lg p-8 text-center transition-all duration-200 cursor-pointer ${
            isDragging ? "border-blue-500 bg-blue-50" : "border-gray-300 hover:border-gray-400 hover:bg-gray-50"
          }`}
          onDragEnter={handleDragEnter} onDragLeave={handleDragLeave} onDragOver={handleDragOver} onDrop={handleDrop}
          onClick={() => document.getElementById("fileInput").click()}
        >
          <div className="flex flex-col items-center space-y-4">
            <div className={`p-3 rounded-lg ${isDragging ? "bg-blue-100" : "bg-gray-100"}`}>
              <Upload className={`w-8 h-8 ${isDragging ? "text-blue-600" : "text-gray-500"}`} />
            </div>
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">면담 결과 Excel 파일 업로드</h3>
              <p className="text-sm text-gray-600 mb-4">.xlsx 또는 .xls 파일을 선택하세요</p>
              <button className="inline-flex items-center px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md hover:bg-blue-700 transition-colors duration-200">
                <FileSpreadsheet className="w-4 h-4 mr-2" />
                파일 선택
              </button>
            </div>
          </div>
          <input id="fileInput" type="file" accept=".xlsx,.xls" onChange={handleFileSelect} className="hidden" />
        </div>
        {loading && (
            <div className="mt-6 flex flex-col items-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                <p className="mt-3 text-sm text-gray-600">{loadingMessage}</p>
            </div>
        )}
      </div>
    );
};

// --- 재사용 가능한 키워드 대시보드 컴포넌트 ---
const KeywordDashboard = ({ title, icon: Icon, filteredData, allKeywordData, opinionColumn }) => {
    const [localJob, setLocalJob] = useState('all');
    const [localYear, setLocalYear] = useState('all');
    const [selectedKeyword, setSelectedKeyword] = useState(null);
    const [relatedOpinions, setRelatedOpinions] = useState([]);

    const localJobOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무 || ""))].filter(Boolean), [filteredData]);
    const localYearOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무연차 || ""))].filter(Boolean), [filteredData]);

    const groupedKeywords = useMemo(() => {
        const locallyFilteredData = filteredData.filter(item => {
            const jobMatch = localJob === 'all' || item.직무 === localJob;
            const yearMatch = localYear === 'all' || item.직무연차 === localYear;
            return jobMatch && yearMatch;
        });

        if (locallyFilteredData.length === 0 || !allKeywordData || Object.keys(allKeywordData).length === 0) {
            return {};
        }

        const keywordMap = locallyFilteredData.reduce((acc, row) => {
            const groupKey = row['직무'] || '기타';
            if (!acc[groupKey]) acc[groupKey] = {};
            const keywords = allKeywordData[row.uniqueId];
            if (keywords && keywords.length > 0) {
                keywords.forEach(({ word, score }) => {
                    if (!acc[groupKey][word]) acc[groupKey][word] = { totalScore: 0, count: 0 };
                    acc[groupKey][word].totalScore += score;
                    acc[groupKey][word].count += 1;
                });
            }
            return acc;
        }, {});

        const finalGroupedKeywords = {};
        for (const group in keywordMap) {
            finalGroupedKeywords[group] = Object.entries(keywordMap[group])
                .map(([word, data]) => ({ word, score: data.totalScore / data.count, count: data.count }))
                .sort((a, b) => b.count - a.count || b.score - a.score)
                .slice(0, 15);
        }
        return finalGroupedKeywords;
    }, [filteredData, allKeywordData, localJob, localYear]);

    const isFilterSelected = localJob !== 'all' || localYear !== 'all';

    const handleKeywordClick = (keyword) => {
        if (selectedKeyword === keyword) {
            setSelectedKeyword(null);
            return;
        }
        const opinions = filteredData
            .filter(row => {
                const jobMatch = localJob === 'all' || row.직무 === localJob;
                const yearMatch = localYear === 'all' || row.직무연차 === localYear;
                const textExists = row[opinionColumn] && typeof row[opinionColumn] === 'string';
                return jobMatch && yearMatch && textExists && row[opinionColumn].includes(keyword);
            })
            .map(row => ({ name: row.이름, job: row.직무, year: row.직무연차, opinion: row[opinionColumn] }));
        setRelatedOpinions(opinions);
        setSelectedKeyword(keyword);
    };

    const KeywordBarChart = ({ keywords, groupName }) => {
        const top5Keywords = keywords.slice(0, 5);
        const maxCount = top5Keywords.length > 0 ? Math.max(...top5Keywords.map(k => k.count)) : 1;
        return (
            <div className="bg-gray-50 rounded-lg p-4 h-full flex flex-col">
                <h4 className="font-medium text-gray-900 mb-4 flex items-center">
                    <BarChart3 className="w-4 h-4 mr-1 text-gray-600" /> {groupName} - 키워드 빈도 Top 5
                </h4>
                <div className="space-y-3 flex-grow">
                    {top5Keywords.map(({ word, count }) => (
                        <div key={word} className="w-full">
                            <div className="flex justify-between items-center mb-1">
                                <span className="text-sm font-medium text-gray-700">{word}</span>
                                <span className="text-sm text-gray-600">{count}회</span>
                            </div>
                            <div className="w-full bg-gray-200 rounded-full h-2">
                                <div className="h-2 bg-blue-500 rounded-full transition-all duration-300" style={{ width: `${(count / maxCount) * 100}%` }} />
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    const numGroups = Object.keys(groupedKeywords).length;

    return (
        <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
            <div className="flex flex-wrap justify-between items-center mb-4 gap-2">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                    <Icon className="w-5 h-5 mr-2 text-blue-600" /> {title}
                </h3>
                <div className="flex items-center gap-2">
                    <select value={localJob} onChange={(e) => setLocalJob(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                        {localJobOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 직무' : option}</option>)}
                    </select>
                    <select value={localYear} onChange={(e) => setLocalYear(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                        {localYearOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 연차' : option}</option>)}
                    </select>
                </div>
            </div>

            {isFilterSelected ? (
                numGroups > 0 ? (
                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                        {numGroups === 1 ? (
                            Object.entries(groupedKeywords).map(([group, keywords]) => (
                                <React.Fragment key={group}>
                                    <div className="bg-gray-50 rounded-lg p-4">
                                        <h4 className="font-medium text-gray-900 mb-3 flex items-center">
                                            <Tag className="w-4 h-4 mr-1 text-gray-600" /> {group} - 주요 키워드
                                        </h4>
                                        <div className="flex flex-wrap gap-2">
                                            {keywords.map(({ word, count }) => (
                                                <button key={word} title={`클릭하여 관련 의견 보기 (빈도: ${count})`}
                                                    onClick={() => handleKeywordClick(word)}
                                                    className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium cursor-pointer border transition-colors ${selectedKeyword === word ? 'bg-blue-600 text-white border-blue-600' : 'bg-gray-100 hover:bg-blue-100 hover:text-blue-800 text-gray-700 border-gray-200'}`}>
                                                    <Hash className="w-3 h-3 mr-1" /> {word}
                                                </button>
                                            ))}
                                        </div>
                                    </div>
                                    <KeywordBarChart keywords={keywords} groupName={group} />
                                </React.Fragment>
                            ))
                        ) : (
                            Object.entries(groupedKeywords).map(([group, keywords]) => (
                                <div key={group} className="bg-gray-50 rounded-lg p-4">
                                    <h4 className="font-medium text-gray-900 mb-3 flex items-center">
                                        <Tag className="w-4 h-4 mr-1 text-gray-600" /> {group}
                                    </h4>
                                    <div className="flex flex-wrap gap-2">
                                        {keywords.map(({ word, count }) => (
                                            <button key={word} title={`클릭하여 관련 의견 보기 (빈도: ${count})`}
                                                onClick={() => handleKeywordClick(word)}
                                                className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium cursor-pointer border transition-colors ${selectedKeyword === word ? 'bg-blue-600 text-white border-blue-600' : 'bg-gray-100 hover:bg-blue-100 hover:text-blue-800 text-gray-700 border-gray-200'}`}>
                                                <Hash className="w-3 h-3 mr-1" /> {word}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                ) : (
                    <div className="text-center py-8"><MessageSquare className="w-12 h-12 mx-auto mb-4 text-gray-300" /><p className="text-gray-500">해당 조건의 분석 데이터가 없습니다.</p></div>
                )
            ) : (
                <div className="text-center py-8 bg-gray-50 rounded-lg"><BarChart3 className="w-12 h-12 mx-auto mb-4 text-gray-300" /><p className="text-gray-500 font-medium">직무 또는 연차를 선택하여 키워드 분석 결과를 확인하세요.</p></div>
            )}

            {selectedKeyword && (
                <div className="mt-4 pt-4 border-t">
                    <div className="flex justify-between items-center mb-2">
                        <h4 className="font-medium text-gray-900"><span className="text-blue-600 font-semibold">"{selectedKeyword}"</span> 관련 의견 ({relatedOpinions.length}건)</h4>
                        <button onClick={() => setSelectedKeyword(null)} className="p-1 rounded-full hover:bg-gray-200"><X className="w-5 h-5 text-gray-500" /></button>
                    </div>
                    <div className="max-h-52 overflow-y-auto pr-2 space-y-2">
                        {relatedOpinions.length > 0 ? (
                            relatedOpinions.map((item, index) => (
                                <div key={index} className="bg-gray-100 p-3 rounded-lg border border-gray-200">
                                    <div className="flex items-center mb-2 text-sm"><User className="w-4 h-4 text-gray-500 mr-2"/><span className="font-semibold text-gray-800">{item.name}</span><span className="text-gray-500 ml-2">({item.job} / {item.year})</span></div>
                                    <p className="text-base text-gray-700 whitespace-pre-wrap">{item.opinion}</p>
                                </div>
                            ))
                        ) : (<p className="text-center text-gray-500 py-4">관련 의견을 찾을 수 없습니다.</p>)}
                    </div>
                </div>
            )}
        </div>
    );
};

// --- 인사이동 희망 여부 분석 컴포넌트 ---
const TransferAnalysis = ({ filteredData, analysisResult }) => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [localJob, setLocalJob] = useState('all');
  const [localYear, setLocalYear] = useState('all');

  const localJobOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무 || ""))].filter(Boolean), [filteredData]);
  const localYearOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무연차 || ""))].filter(Boolean), [filteredData]);

  const displayData = useMemo(() => {
    const locallyFilteredData = filteredData.filter(item => {
      const jobMatch = localJob === 'all' || item.직무 === localJob;
      const yearMatch = localYear === 'all' || item.직무연차 === localYear;
      return jobMatch && yearMatch;
    });
    const locallyFilteredIds = new Set(locallyFilteredData.map(item => item.uniqueId));
    const hopefuls = analysisResult.transfer_hopefuls.filter(user => locallyFilteredIds.has(user.uniqueId));
    const total = locallyFilteredData.length;
    const hopeCount = hopefuls.length;
    return { hopefuls, total, hopeCount, hopePercentage: total > 0 ? Math.round((hopeCount / total) * 100) : 0 };
  }, [filteredData, analysisResult, localJob, localYear]);

  const DonutChart = ({ percentage }) => (
    <div className="relative w-24 h-24 flex items-center justify-center">
      <svg className="w-full h-full" viewBox="0 0 36 36" transform="rotate(-90)"><path d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" fill="none" stroke="#e5e7eb" strokeWidth="3" /><path d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831" fill="none" stroke="#3b82f6" strokeWidth="3" strokeDasharray={`${percentage}, 100`} /></svg>
      <div className="absolute text-center transform rotate(90)"><span className="block text-xl font-bold text-blue-600">{displayData.hopePercentage}%</span><span className="text-xs text-gray-500">희망</span></div>
    </div>
  );

  const downloadHopefulsCSV = () => {
    if (displayData.hopefuls.length === 0) { alert("다운로드할 인사이동 희망자 데이터가 없습니다."); return; }
    const dataToExport = displayData.hopefuls.map(({ uniqueId, ...rest }) => rest);
    const csv = Papa.unparse(dataToExport);
    const blob = new Blob([`\uFEFF${csv}`], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "transfer_hopefuls_data.csv";
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
      <div className="flex flex-wrap justify-between items-center mb-4 gap-2">
        <h3 className="text-lg font-semibold text-gray-900">인사이동 희망 여부 분석</h3>
        <div className="flex items-center gap-2">
          <select value={localJob} onChange={(e) => setLocalJob(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">{localJobOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 직무' : option}</option>)}</select>
          <select value={localYear} onChange={(e) => setLocalYear(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">{localYearOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 연차' : option}</option>)}</select>
          <button onClick={downloadHopefulsCSV} className="inline-flex items-center px-3 py-1.5 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 transition-colors"><Download className="w-4 h-4 mr-2" />CSV</button>
        </div>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="flex items-center justify-center space-x-4 bg-gray-50 p-4 rounded-lg">
          <div className="flex flex-col items-center"><UserCheck className="w-7 h-7 text-blue-500 mb-1" /><span className="font-bold text-lg text-blue-600">{displayData.hopeCount}</span><span className="text-xs text-gray-500">희망자</span></div>
          <DonutChart percentage={displayData.hopePercentage} />
          <div className="flex flex-col items-center"><User className="w-7 h-7 text-gray-400 mb-1" /><span className="font-bold text-lg text-gray-600">{displayData.total - displayData.hopeCount}</span><span className="text-xs text-gray-500">기타</span></div>
        </div>
        <div className="md:col-span-2">
          <h4 className="font-medium text-gray-800 mb-3">인사이동 희망자 목록 ({displayData.hopeCount}명)</h4>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 max-h-48 overflow-y-auto pr-2">
            {displayData.hopefuls.length > 0 ? (
              displayData.hopefuls.map(user => (
                <div key={user.uniqueId} onClick={() => setSelectedUser(user)} className="flex items-center p-3 rounded-lg border border-gray-200 bg-white hover:bg-blue-50 hover:border-blue-300 cursor-pointer transition-all shadow-sm">
                  <div className="flex-shrink-0 mr-3"><div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center"><User className="w-5 h-5 text-blue-600" /></div></div>
                  <div className="flex-grow min-w-0"><p className="font-semibold text-gray-800 truncate">{user.이름}</p><p className="text-sm text-gray-500 truncate">{user.직무} / {user.직무연차}</p></div>
                </div>
              ))
            ) : (<div className="flex items-center justify-center h-full"><p className="text-center text-gray-500 py-4">해당 조건의 인사이동 희망자가 없습니다.</p></div>)}
          </div>
        </div>
      </div>
      {selectedUser && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4" onClick={() => setSelectedUser(null)}>
          <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-lg" onClick={e => e.stopPropagation()}>
            <div className="flex justify-between items-start">
              <div><h3 className="text-xl font-bold text-gray-900">{selectedUser.이름}</h3><p className="text-sm text-gray-600 mt-1">{selectedUser.직무} / {selectedUser.직무연차}</p></div>
              <button onClick={() => setSelectedUser(null)} className="p-1 rounded-full hover:bg-gray-200"><X className="w-5 h-5 text-gray-500" /></button>
            </div>
            <div className="mt-4 pt-4 border-t">
              <p className="text-sm font-semibold text-gray-700 mb-2">(2) 성장/역량/커리어-구성원 의견</p>
              <div className="max-h-60 overflow-y-auto bg-gray-50 p-3 rounded-md"><p className="text-base text-gray-800 whitespace-pre-wrap">{selectedUser['(2) 성장/역량/커리어-구성원 의견']}</p></div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// --- 메인 차트 섹션 컴포넌트 ---
const ChartSection = ({ 
  filteredData, 
  transferAnalysisData, 
  workKeywords, 
  growthKeywords, 
  envKeywords 
}) => {
  const jobStats = filteredData.reduce((acc, item) => {
    const job = item.직무 || "기타";
    acc[job] = (acc[job] || 0) + 1;
    return acc;
  }, {});
  const yearStats = filteredData.reduce((acc, item) => {
    const year = item.직무연차 || "미정";
    acc[year] = (acc[year] || 0) + 1;
    return acc;
  }, {});
  const StatCard = ({ title, value, icon: Icon }) => (
    <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
      <div className="flex items-center justify-between">
        <div><p className="text-sm font-medium text-gray-600">{title}</p><p className="text-2xl font-semibold text-gray-900 mt-1">{value}</p></div>
        <div className="p-2 bg-blue-50 rounded-lg"><Icon className="w-5 h-5 text-blue-600" /></div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <StatCard title="총 면담자 수" value={filteredData.length.toLocaleString()} icon={Users} />
        <StatCard title="직무 유형" value={Object.keys(jobStats).length} icon={BarChart3} />
        <StatCard title="연차 범위" value={Object.keys(yearStats).length} icon={Clock} />
      </div>
      {transferAnalysisData && <TransferAnalysis filteredData={filteredData} analysisResult={transferAnalysisData} />}
      {workKeywords && <KeywordDashboard title="업무 관련 키워드 분석" icon={Briefcase} filteredData={filteredData} allKeywordData={workKeywords} opinionColumn="(1) 업무-구성원 의견" />}
      {growthKeywords && <KeywordDashboard title="성장/역량 관련 키워드 분석" icon={TrendingUp} filteredData={filteredData} allKeywordData={growthKeywords} opinionColumn="(2) 성장/역량/커리어-구성원 의견" />}
      {envKeywords && <KeywordDashboard title="업무환경 관련 키워드 분석" icon={Home} filteredData={filteredData} allKeywordData={envKeywords} opinionColumn="(3) 업무환경조성-구성원 의견" />}
    </div>
  );
};

// --- 메인 앱 컴포넌트 ---
const App = () => {
  const [data, setData] = useState([]);
  const [transferAnalysisData, setTransferAnalysisData] = useState(null);
  const [workKeywords, setWorkKeywords] = useState(null);
  const [growthKeywords, setGrowthKeywords] = useState(null);
  const [envKeywords, setEnvKeywords] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState("");
  const [selectedJob, setSelectedJob] = useState("all");
  const [selectedYear, setSelectedYear] = useState("all");

  const jobOptions = useMemo(() => ["all", ...new Set(data.map((item) => item.직무 || ""))].filter(Boolean), [data]);
  const yearOptions = useMemo(() => ["all", ...new Set(data.map((item) => item.직무연차 || ""))].filter(Boolean), [data]);

  const filteredData = useMemo(() => data.filter((item) => {
    const jobMatch = selectedJob === "all" || item.직무 === selectedJob;
    const yearMatch = selectedYear === 'all' || item.직무연차 === selectedYear;
    return jobMatch && yearMatch;
  }), [data, selectedJob, selectedYear]);

  const handleFileUpload = (file) => {
    setLoading(true);
    setLoadingMessage("Excel 파일을 파싱 중입니다...");

    const reader = new FileReader();
    reader.onload = async (e) => {
        try {
            const bytes = e.target.result;
            const workbook = XLSX.read(bytes, { type: "binary" });
            const sheet = workbook.Sheets[workbook.SheetNames[0]];
            let jsonData = XLSX.utils.sheet_to_json(sheet);
            jsonData = jsonData.map((row, index) => ({ ...row, uniqueId: `row_${index}` }));

            setLoadingMessage("AI 모델로 데이터 분석 중입니다...");

            const analysisPromises = [
                runAnalysisForRowByRow(jsonData, '(1) 업무-구성원 의견'),
                runAnalysisForRowByRow(jsonData, '(2) 성장/역량/커리어-구성원 의견'),
                runAnalysisForRowByRow(jsonData, '(3) 업무환경조성-구성원 의견'),
                fetch('http://127.0.0.1:8000/analyze-transfer-intent', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ data: jsonData }),
                }).then(res => {
                    if (!res.ok) throw new Error('인사이동 분석 API 실패');
                    return res.json();
                })
            ];

            const [workResult, growthResult, envResult, transferResult] = await Promise.all(analysisPromises);

            setWorkKeywords(workResult);
            setGrowthKeywords(growthResult);
            setEnvKeywords(envResult);
            setTransferAnalysisData(transferResult);

            setData(jsonData);

        } catch (error) {
            console.error("파일 처리 에러:", error);
            alert(`파일 처리 중 오류가 발생했습니다: ${error.message}`);
        } finally {
            setLoading(false);
            setLoadingMessage("");
        }
    };
    reader.readAsBinaryString(file);
  };

  const downloadCSV = () => {
    const csv = Papa.unparse(filteredData);
    const blob = new Blob([`\uFEFF${csv}`], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "filtered_interview_data.csv";
    link.click();
    URL.revokeObjectURL(url);
  };

  const SkLogo = () => (
    <img src="https://www.skenmove.com/images/common/logo-re.png" alt="SK enmove" className="w-auto h-8" />
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <SkLogo />
              <h1 className="text-xl font-semibold text-gray-900">면담결과 대시보드</h1>
            </div>
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-600"><Bell className="w-5 h-5" /></button>
              <button className="p-2 text-gray-400 hover:text-gray-600"><Settings className="w-5 h-5" /></button>
              <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center"><User className="w-4 h-4 text-gray-600" /></div>
            </div>
          </div>
        </div>
      </nav>
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {data.length === 0 ? (
          <FileUploader onFileUpload={handleFileUpload} loading={loading} loadingMessage={loadingMessage} />
        ) : (
          <>
            <div className="mb-6 bg-white rounded-lg p-4 shadow-sm border border-gray-200">
                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
                    <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-4">
                    <div className="min-w-0 flex-1 sm:max-w-xs">
                        <label className="block text-xs font-medium text-gray-700 mb-1">직무</label>
                        <select value={selectedJob} onChange={(e) => setSelectedJob(e.target.value)} className="block w-full text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                        {jobOptions.map((option) => <option key={option} value={option}>{option === "all" ? "전체 직무" : option}</option>)}
                        </select>
                    </div>
                    <div className="min-w-0 flex-1 sm:max-w-xs">
                        <label className="block text-xs font-medium text-gray-700 mb-1">연차</label>
                        <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)} className="block w-full text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                        {yearOptions.map((option) => <option key={option} value={option}>{option === "all" ? "전체 연차" : option}</option>)}
                        </select>
                    </div>
                    </div>
                    {filteredData.length > 0 && (
                    <button onClick={downloadCSV} className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200">
                        <Download className="w-4 h-4 mr-2" /> CSV 다운로드
                    </button>
                    )}
                </div>
            </div>
            {filteredData.length === 0 ? (
                <div className="bg-white rounded-lg p-12 text-center shadow-sm border border-gray-200">
                    <BarChart3 className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">데이터가 없습니다</h3>
                    <p className="text-gray-500">선택된 필터 조건에 맞는 데이터가 없습니다.</p>
                </div>
            ) : (
                <ChartSection 
                  filteredData={filteredData} 
                  transferAnalysisData={transferAnalysisData}
                  workKeywords={workKeywords}
                  growthKeywords={growthKeywords}
                  envKeywords={envKeywords}
                />
            )}
          </>
        )}
      </main>
    </div>
  );
};

export default App;
