import React, { useState } from "react";
import * as XLSX from "xlsx";
import Papa from "papaparse";
import nlp from "compromise"; // 외부 패키지 변경: npm i compromise
import { Upload, Download, BarChart3, FileSpreadsheet, TrendingUp, Users, Clock, Bell, User, Settings, MessageSquare, Tag, Hash } from "lucide-react";

// 한국어 불용어 목록 (키워드 추출에 적용)
const koreanStopwords = new Set([
  '그리고', '하지만', '그러나', '또한', '이것', '그것', '저것', '이런', '그런', '저런', 
  '것은', '것이', '것을', '때문', '경우', '방법', '문제', '상황', '결과', '하는', '있다', 
  '이다', '에서', '으로', '하고', '이다', '등', '수', '더', '많이', '조금', '매우', '아주'
]);

// --- ✨ [변경] compromise 라이브러리를 사용한 키워드 추출 함수 ---
const extractKeywords = (text, maxKeywords = 10) => {
  if (!text || typeof text !== 'string') return [];

  // compromise가 기본적인 전처리를 하지만, 소문자 변환 및 공백 제거
  const cleanText = text.toLowerCase().trim();
  if (!cleanText) return [];

  // 1. compromise를 사용하여 텍스트를 분석하고 명사 목록을 빈도수와 함께 추출합니다.
  const doc = nlp(cleanText);
  const terms = doc.nouns().out('frequency'); // 결과: [{ normal: '단어', count: 빈도수 }]

  // 2. 불용어와 짧은 단어를 필터링합니다.
  const filteredTerms = terms.filter(term => 
    term.normal.length >= 2 && !koreanStopwords.has(term.normal)
  );

  if (filteredTerms.length === 0) return [];

  // 3. 빈도수(count)를 기준으로 내림차순 정렬합니다. (compromise가 이미 해주지만 확실하게)
  const sortedTerms = filteredTerms.sort((a, b) => b.count - a.count);

  // 4. 점수 정규화를 위해 가장 높은 빈도수를 찾습니다.
  const maxScore = sortedTerms[0].count;

  // 5. 상위 키워드를 기존 컴포넌트 형식에 맞게 변환하여 반환합니다.
  return sortedTerms
    .slice(0, maxKeywords)
    .map(term => ({
      word: term.normal,
      score: Math.round((term.count / maxScore) * 100) / 100, // 점수를 0~1 사이로 정규화
    }));
};

// compromise 라이브러리를 사용한 고도화된 키워드 추출 함수 (N-gram 활용)
const extractKeywordsV2 = (text, maxKeywords = 10) => {
  if (!text || typeof text !== 'string') return [];

  const cleanText = text.toLowerCase().trim();
  if (!cleanText) return [];

  const doc = nlp(cleanText);

  // 1. Unigrams (단일 명사) 추출 및 점수 계산
  const nouns = doc.nouns().out('frequency');
  const unigrams = nouns
    .filter(term => term.normal.length >= 2 && !koreanStopwords.has(term.normal))
    .map(term => ({
      text: term.normal,
      score: term.count, // 기본 점수는 빈도수
    }));

  // 2. Bigrams (2-gram, 두 단어 구문) 추출 및 점수 계산
  const bigrams = doc.ngrams({ size: 2 }) // size를 2로 설정
    .filter(ngram => {
      // 두 단어 모두 2글자 이상이고, 불용어를 포함하지 않는 경우만 선택
      const words = ngram.normal.split(' ');
      return words.length === 2 &&
             words.every(word => word.length >= 2 && !koreanStopwords.has(word));
    })
    .out('frequency')
    .map(term => ({
      text: term.normal,
      score: term.count * 1.5, // 구문(Bigram)에 1.5배 가중치 부여!
    }));

  // 3. Unigram과 Bigram 목록을 합친 후 점수 기준으로 정렬
  const combined = [...unigrams, ...bigrams]
    // 중복된 구문/단어가 있다면 더 높은 점수를 가진 것을 선택 (거의 발생 안 함)
    .reduce((acc, current) => {
      const existing = acc.find(item => item.text === current.text);
      if (!existing || current.score > existing.score) {
        return [...acc.filter(item => item.text !== current.text), current];
      }
      return acc;
    }, [])
    .sort((a, b) => b.score - a.score);

  if (combined.length === 0) return [];

  // 4. 최종 점수 정규화 및 반환
  const maxScore = combined[0].score;
  return combined
    .slice(0, maxKeywords)
    .map(term => ({
      word: term.text, // 'word' 키를 사용해 기존 컴포넌트와 호환
      score: Math.round((term.score / maxScore) * 100) / 100,
    }));
};

// 텍스트 컬럼들을 찾는 함수
const findTextColumns = (data) => {
  if (data.length === 0) return [];

  const sampleRow = data[0];
  const textColumns = [];

  Object.keys(sampleRow).forEach(key => {
    const sampleValue = sampleRow[key];
    if (typeof sampleValue === 'string' && sampleValue.length > 10) {
      // 텍스트가 충분히 길고 의견, 코멘트, 피드백 등의 키워드가 포함된 컬럼 우선
      const isLikelyTextColumn = /의견|코멘트|피드백|내용|설명|답변|comment|feedback|opinion/i.test(key);
      textColumns.push({ key, priority: isLikelyTextColumn ? 1 : 0 });
    }
  });

  return textColumns.sort((a, b) => b.priority - a.priority).map(col => col.key);
};

const FileUploader = ({ onFileUpload }) => {
  const [isDragging, setIsDragging] = useState(false);

  const handleDragEnter = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      onFileUpload(files[0]);
    }
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      onFileUpload(file);
    }
  };

  return (
    <div
      className={`relative border-2 border-dashed rounded-lg p-8 text-center transition-all duration-200 cursor-pointer ${
        isDragging
          ? "border-blue-500 bg-blue-50"
          : "border-gray-300 hover:border-gray-400 hover:bg-gray-50"
      }`}
      onDragEnter={handleDragEnter}
      onDragLeave={handleDragLeave}
      onDragOver={handleDragOver}
      onDrop={handleDrop}
      onClick={() => document.getElementById("fileInput").click()}
    >
      <div className="flex flex-col items-center space-y-4">
        <div className={`p-3 rounded-lg ${
          isDragging ? "bg-blue-100" : "bg-gray-100"
        }`}>
          <Upload className={`w-8 h-8 ${
            isDragging ? "text-blue-600" : "text-gray-500"
          }`} />
        </div>
        <div>
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            Excel 파일 업로드
          </h3>
          <p className="text-sm text-gray-600 mb-4">
            .xlsx 또는 .xls 파일을 선택하세요
          </p>
          <button className="inline-flex items-center px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md hover:bg-blue-700 transition-colors duration-200">
            <FileSpreadsheet className="w-4 h-4 mr-2" />
            파일 선택
          </button>
        </div>
      </div>
      <input
        id="fileInput"
        type="file"
        accept=".xlsx,.xls"
        onChange={handleFileSelect}
        className="hidden"
      />
    </div>
  );
};

const KeywordAnalysis = ({ filteredData }) => {
  const [selectedTextColumn, setSelectedTextColumn] = useState('');
  const [groupByColumn, setGroupByColumn] = useState('직무');

  const textColumns = findTextColumns(filteredData);
  const groupByOptions = ['직무', '직무연차', ...Object.keys(filteredData[0] || {}).filter(key => 
    !textColumns.includes(key) && filteredData.filter(item => item[key]).length > filteredData.length * 0.7
  )];

  // 기본 텍스트 컬럼 설정
  React.useEffect(() => {
    if (textColumns.length > 0 && !selectedTextColumn) {
      setSelectedTextColumn(textColumns[0]);
    }
  }, [textColumns, selectedTextColumn]);

  if (textColumns.length === 0) {
    return (
      <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
          <MessageSquare className="w-5 h-5 mr-2 text-blue-600" />
          키워드 분석
        </h3>
        <div className="text-center py-8">
          <MessageSquare className="w-12 h-12 mx-auto mb-4 text-gray-300" />
          <p className="text-gray-500">분석할 텍스트 데이터가 없습니다.</p>
          <p className="text-sm text-gray-400 mt-1">의견, 코멘트 등의 텍스트 컬럼이 필요합니다.</p>
        </div>
      </div>
    );
  }

  if (!selectedTextColumn) return null;

  // 그룹별 키워드 추출
  const groupedKeywords = {};
  const groupedData = filteredData.reduce((acc, item) => {
    const groupKey = item[groupByColumn] || '기타';
    if (!acc[groupKey]) acc[groupKey] = [];
    if (item[selectedTextColumn]) {
      acc[groupKey].push(item[selectedTextColumn]);
    }
    return acc;
  }, {});

  Object.entries(groupedData).forEach(([group, texts]) => {
    const combinedText = texts.join(' ');
    const keywords = extractKeywordsV2(combinedText, 8);
    if (keywords.length > 0) {
      groupedKeywords[group] = keywords;
    }
  });

  const KeywordCloud = ({ keywords, title }) => (
    <div className="bg-gray-50 rounded-lg p-4">
      <h4 className="font-medium text-gray-900 mb-3 flex items-center">
        <Tag className="w-4 h-4 mr-1 text-blue-600" />
        {title}
      </h4>
      <div className="flex flex-wrap gap-2">
        {keywords.map(({ word, score }, index) => (
          <span
            key={word}
            className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium ${
              index < 3 
                ? 'bg-blue-100 text-blue-800 border border-blue-200'
                : 'bg-gray-100 text-gray-700 border border-gray-200'
            }`}
            style={{
              fontSize: `${Math.max(12, 12 + score * 4)}px` // 폰트 크기 조절 로직을 점수에 맞게 단순화
            }}
          >
            <Hash className="w-3 h-3 mr-1" />
            {word}
          </span>
        ))}
      </div>
    </div>
  );

  return (
    <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-gray-900 flex items-center">
          <MessageSquare className="w-5 h-5 mr-2 text-blue-600" />
          키워드 분석
        </h3>
        <div className="flex space-x-3">
          <div className="min-w-0">
            <label className="block text-xs font-medium text-gray-700 mb-1">
              분석할 텍스트
            </label>
            <select
              value={selectedTextColumn}
              onChange={(e) => setSelectedTextColumn(e.target.value)}
              className="block text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
            >
              {textColumns.map((column) => (
                <option key={column} value={column}>
                  {column}
                </option>
              ))}
            </select>
          </div>
          <div className="min-w-0">
            <label className="block text-xs font-medium text-gray-700 mb-1">
              그룹별 분석
            </label>
            <select
              value={groupByColumn}
              onChange={(e) => setGroupByColumn(e.target.value)}
              className="block text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
            >
              {groupByOptions.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {Object.keys(groupedKeywords).length === 0 ? (
        <div className="text-center py-8">
          <MessageSquare className="w-12 h-12 mx-auto mb-4 text-gray-300" />
          <p className="text-gray-500">분석할 텍스트가 충분하지 않습니다.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
          {Object.entries(groupedKeywords)
            .sort(([,a], [,b]) => b.length - a.length)
            .map(([group, keywords]) => (
            <KeywordCloud
              key={group}
              title={`${group} (${keywords.length}개 키워드)`}
              keywords={keywords}
            />
          ))}
        </div>
      )}
    </div>
  );
};

const ChartSection = ({ filteredData }) => {
  // 직무별 통계
  const jobStats = filteredData.reduce((acc, item) => {
    const job = item.직무 || "기타";
    acc[job] = (acc[job] || 0) + 1;
    return acc;
  }, {});

  // 직무연차별 통계
  const yearStats = filteredData.reduce((acc, item) => {
    const year = item.직무연차 || "미정";
    acc[year] = (acc[year] || 0) + 1;
    return acc;
  }, {});

  const StatCard = ({ title, value, icon: Icon, change }) => (
    <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-semibold text-gray-900 mt-1">{value}</p>
          {change && (
            <p className="text-xs text-green-600 mt-1">+{change}% from last month</p>
          )}
        </div>
        <div className="p-2 bg-blue-50 rounded-lg">
          <Icon className="w-5 h-5 text-blue-600" />
        </div>
      </div>
    </div>
  );

  const ChartBar = ({ label, value, maxValue, percentage }) => (
    <div className="mb-4">
      <div className="flex justify-between items-center mb-2">
        <span className="text-sm font-medium text-gray-700">{label}</span>
        <span className="text-sm text-gray-600">{value} ({percentage}%)</span>
      </div>
      <div className="w-full bg-gray-200 rounded-full h-2">
        <div
          className="h-2 bg-blue-600 rounded-full transition-all duration-300"
          style={{ width: `${(value / maxValue) * 100}%` }}
        />
      </div>
    </div>
  );

  const maxJobValue = Math.max(...Object.values(jobStats));
  const maxYearValue = Math.max(...Object.values(yearStats));

  return (
    <div className="space-y-6">
      {/* 통계 카드 */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <StatCard
          title="총 면담자 수"
          value={filteredData.length.toLocaleString()}
          icon={Users}
          change="12"
        />
        <StatCard
          title="직무 유형"
          value={Object.keys(jobStats).length}
          icon={BarChart3}
        />
        <StatCard
          title="연차 범위"
          value={Object.keys(yearStats).length}
          icon={Clock}
        />
      </div>

      {/* 차트 섹션 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-gray-900">직무별 분포</h3>
            <TrendingUp className="w-5 h-5 text-gray-400" />
          </div>
          <div className="space-y-4">
            {Object.entries(jobStats)
              .sort(([,a], [,b]) => b - a)
              .map(([job, count]) => (
              <ChartBar
                key={job}
                label={job}
                value={count}
                maxValue={maxJobValue}
                percentage={Math.round((count / filteredData.length) * 100)}
              />
            ))}
          </div>
        </div>

        <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-gray-900">직무연차별 분포</h3>
            <Clock className="w-5 h-5 text-gray-400" />
          </div>
          <div className="space-y-4">
            {Object.entries(yearStats)
              .sort(([,a], [,b]) => b - a)
              .map(([year, count]) => (
              <ChartBar
                key={year}
                label={year}
                value={count}
                maxValue={maxYearValue}
                percentage={Math.round((count / filteredData.length) * 100)}
              />
            ))}
          </div>
        </div>
      </div>

      {/* 키워드 분석 섹션 추가 */}
      <KeywordAnalysis filteredData={filteredData} />
    </div>
  );
};

const App = () => {
  const [uploaded, setUploaded] = useState(false);
  const [data, setData] = useState([]);
  const [selectedJob, setSelectedJob] = useState("all");
  const [selectedYear, setSelectedYear] = useState("all");
  const [loading, setLoading] = useState(false);

  const jobOptions = ["all", ...new Set(data.map((item) => item.직무 || ""))];
  const yearOptions = ["all", ...new Set(data.map((item) => item.직무연차 || ""))];

  const filteredData = data.filter((item) => {
    const jobMatch = selectedJob === "all" || item.직무 === selectedJob;
    const yearMatch = selectedYear === "all" || item.직무연차 === selectedYear;
    return jobMatch && yearMatch;
  });

  const handleFileUpload = (file) => {
    setLoading(true);
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const bytes = e.target.result;
        const workbook = XLSX.read(bytes, { type: "binary" });
        const sheet = workbook.Sheets[workbook.SheetNames[0]];
        const parsedData = XLSX.utils.sheet_to_json(sheet, { header: 1 });

        const headers = parsedData[0];
        const rows = parsedData.slice(1).filter((row) => row.some((cell) => cell));
        const jsonData = rows.map((row) =>
          headers.reduce((obj, header, index) => {
            obj[header] = row[index] || "";
            return obj;
          }, {})
        );

        setData(jsonData);
        setUploaded(true);
        console.log("파싱된 Excel 데이터:", jsonData);
      } catch (error) {
        console.error("Excel 파싱 에러:", error);
        alert("파일 파싱에 실패했습니다. Excel 형식을 확인하세요.");
      } finally {
        setLoading(false);
      }
    };
    reader.readAsBinaryString(file);
  };

  const downloadCSV = () => {
    const csv = Papa.unparse(filteredData);
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "filtered_interview_data.csv";
    link.click();
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 네비게이션 바 */}
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-3">
                <div className="w-8 h-8 flex items-center justify-center">
                  <img src="logo.svg" alt="SK엔무브 로고" className="w-8 h-8" />
                </div>
                <div>
                  <h1 className="text-xl font-semibold text-gray-900">SK엔무브</h1>
                  <p className="text-xs text-gray-500">면담결과 대시보드</p>
                </div>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-600">
                <Bell className="w-5 h-5" />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600">
                <Settings className="w-5 h-5" />
              </button>
              <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center">
                <User className="w-4 h-4 text-gray-600" />
              </div>
            </div>
          </div>
        </div>
      </nav>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* 페이지 헤더 */}
        <div className="mb-8">
          <div className="md:flex md:items-center md:justify-between">
            <div className="flex-1 min-w-0">
              <h2 className="text-2xl font-bold leading-7 text-gray-900 sm:text-3xl sm:truncate">
                면담 데이터 분석
              </h2>
              <div className="mt-1 flex flex-col sm:flex-row sm:flex-wrap sm:mt-0 sm:space-x-6">
                <div className="mt-2 flex items-center text-sm text-gray-500">
                  <BarChart3 className="flex-shrink-0 mr-1.5 h-4 w-4 text-gray-400" />
                  데이터 시각화 및 분석 도구
                </div>
              </div>
            </div>
          </div>
        </div>

        {!uploaded ? (
          <div className="max-w-lg mx-auto">
            <FileUploader onFileUpload={handleFileUpload} />
            {loading && (
              <div className="mt-6 flex flex-col items-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                <p className="mt-3 text-sm text-gray-600">파일을 분석 중입니다...</p>
              </div>
            )}
          </div>
        ) : (
          <>
            {/* 필터 및 액션 바 */}
            <div className="mb-6 bg-white rounded-lg p-4 shadow-sm border border-gray-200">
              <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
                <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-4">
                  <div className="min-w-0 flex-1 sm:max-w-xs">
                    <label className="block text-xs font-medium text-gray-700 mb-1">
                      직무
                    </label>
                    <select
                      value={selectedJob}
                      onChange={(e) => setSelectedJob(e.target.value)}
                      className="block w-full text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                    >
                      {jobOptions.map((option) => (
                        <option key={option} value={option}>
                          {option === "all" ? "전체 직무" : option}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="min-w-0 flex-1 sm:max-w-xs">
                    <label className="block text-xs font-medium text-gray-700 mb-1">
                      연차
                    </label>
                    <select
                      value={selectedYear}
                      onChange={(e) => setSelectedYear(e.target.value)}
                      className="block w-full text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                    >
                      {yearOptions.map((option) => (
                        <option key={option} value={option}>
                          {option === "all" ? "전체 연차" : option}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
                {filteredData.length > 0 && (
                  <button
                    onClick={downloadCSV}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200"
                  >
                    <Download className="w-4 h-4 mr-2" />
                    CSV 다운로드
                  </button>
                )}
              </div>
            </div>

            {/* 데이터 시각화 */}
            {filteredData.length === 0 ? (
              <div className="bg-white rounded-lg p-12 text-center shadow-sm border border-gray-200">
                <div className="w-12 h-12 mx-auto mb-4 bg-gray-100 rounded-lg flex items-center justify-center">
                  <BarChart3 className="w-6 h-6 text-gray-400" />
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">데이터가 없습니다</h3>
                <p className="text-gray-500">선택된 필터 조건에 맞는 데이터가 없습니다.</p>
              </div>
            ) : (
              <ChartSection filteredData={filteredData} />
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default App;
