import React, { useState, useEffect, useMemo } from "react";
import * as XLSX from "xlsx";
import Papa from "papaparse";
import { Upload, Download, BarChart3, FileSpreadsheet, TrendingUp, Users, UserCheck, Clock, Bell, User, Settings, MessageSquare, Tag, Hash, BrainCircuit, Zap, X, Briefcase, Home } from "lucide-react";

// --- 한 줄(row)마다 키워드 추출 API를 호출하는 함수 ---
const runAnalysisForRowByRow = async (data, textColumn, mode) => {
  if (!data || data.length === 0 || !textColumn) {
    return {};
  }
  const textsToProcess = data
    .map((row) => ({
      id: row.uniqueId,
      text: row[textColumn],
    }))
    .filter(item => item.text && typeof item.text === 'string' && item.text.trim().length >= 5);

  if (textsToProcess.length === 0) {
    console.log(`'${textColumn}'에 대한 키워드 추출용 텍스트 데이터가 충분하지 않습니다.`);
    return {};
  }

  const endpointMap = {
    llm: '/extract-keywords-llm',
    pos: '/extract-keywords-pos',
    textrank: '/extract-keywords-textrank'
  };
  const endpoint = endpointMap[mode] || endpointMap.llm;

  const allKeywordData = {};

  // Promise.all을 사용하여 병렬 처리
  await Promise.all(textsToProcess.map(async (item) => {
    try {
      const response = await fetch(`http://127.0.0.1:8000${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: item.text }),
      });

      if (response.ok) {
        allKeywordData[item.id] = await response.json();
      } else {
        const errorData = await response.json();
        console.error(`API 오류 (ID: ${item.id}, Column: ${textColumn}):`, errorData.detail || response.statusText);
        allKeywordData[item.id] = [];
      }
    } catch (error) {
      console.error(`네트워크 오류 (ID: ${item.id}, Column: ${textColumn}):`, error);
      allKeywordData[item.id] = [];
    }
  }));

  return allKeywordData;
};

const FileUploader = ({ onFileUpload }) => {
    const [isDragging, setIsDragging] = useState(false);
    const handleDragEnter = (e) => { e.preventDefault(); setIsDragging(true); };
    const handleDragLeave = (e) => { e.preventDefault(); setIsDragging(false); };
    const handleDragOver = (e) => { e.preventDefault(); };
    const handleDrop = (e) => {
      e.preventDefault();
      setIsDragging(false);
      const files = e.dataTransfer.files;
      if (files.length > 0) onFileUpload(files[0]);
    };
    const handleFileSelect = (e) => {
      const file = e.target.files[0];
      if (file) onFileUpload(file);
    };
    return (
      <div
        className={`relative border-2 border-dashed rounded-lg p-8 text-center transition-all duration-200 cursor-pointer ${
          isDragging ? "border-blue-500 bg-blue-50" : "border-gray-300 hover:border-gray-400 hover:bg-gray-50"
        }`}
        onDragEnter={handleDragEnter} onDragLeave={handleDragLeave} onDragOver={handleDragOver} onDrop={handleDrop}
        onClick={() => document.getElementById("fileInput").click()}
      >
        <div className="flex flex-col items-center space-y-4">
          <div className={`p-3 rounded-lg ${isDragging ? "bg-blue-100" : "bg-gray-100"}`}>
            <Upload className={`w-8 h-8 ${isDragging ? "text-blue-600" : "text-gray-500"}`} />
          </div>
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Excel 파일 업로드</h3>
            <p className="text-sm text-gray-600 mb-4">.xlsx 또는 .xls 파일을 선택하세요</p>
            <button className="inline-flex items-center px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md hover:bg-blue-700 transition-colors duration-200">
              <FileSpreadsheet className="w-4 h-4 mr-2" />
              파일 선택
            </button>
          </div>
        </div>
        <input id="fileInput" type="file" accept=".xlsx,.xls" onChange={handleFileSelect} className="hidden" />
      </div>
    );
};

// ✨ [신규] 재사용 가능한 키워드 대시보드 컴포넌트
// ✨ [신규] 재사용 가능한 키워드 대시보드 컴포넌트 (조건부 렌더링 적용)
const KeywordDashboard = ({ title, icon: Icon, filteredData, allKeywordData }) => {
  const [localJob, setLocalJob] = useState('all');
  const [localYear, setLocalYear] = useState('all');

  // ✨ [신규] 보기 기준(그룹핑 기준) 상태 추가
  const [groupBy, setGroupBy] = useState('직무'); 

  const localJobOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무 || ""))].filter(Boolean), [filteredData]);
  const localYearOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무연차 || ""))].filter(Boolean), [filteredData]);

  const groupedKeywords = useMemo(() => {
      const locallyFilteredData = filteredData.filter(item => {
          const jobMatch = localJob === 'all' || item.직무 === localJob;
          const yearMatch = localYear === 'all' || item.직무연차 === localYear;
          return jobMatch && yearMatch;
      });

      if (locallyFilteredData.length === 0 || !allKeywordData || Object.keys(allKeywordData).length === 0) {
          return {};
      }

      const keywordMap = locallyFilteredData.reduce((acc, row) => {
          // ✨ [수정] 그룹핑 기준을 '보기 기준' 상태(groupBy)에 따라 동적으로 변경
          const groupKey = row[groupBy] || '기타';
          if (!acc[groupKey]) acc[groupKey] = {};

          const keywords = allKeywordData[row.uniqueId];
          if (keywords && keywords.length > 0) {
              keywords.forEach(({ word, score }) => {
                  if (!acc[groupKey][word]) acc[groupKey][word] = { totalScore: 0, count: 0 };
                  acc[groupKey][word].totalScore += score;
                  acc[groupKey][word].count += 1;
              });
          }
          return acc;
      }, {});

      const finalGroupedKeywords = {};
      for (const group in keywordMap) {
          finalGroupedKeywords[group] = Object.entries(keywordMap[group])
              .map(([word, data]) => ({
                  word,
                  score: data.totalScore / data.count,
                  count: data.count,
              }))
              .sort((a, b) => b.count - a.count || b.score - a.score)
              .slice(0, 15);
      }
      return finalGroupedKeywords;
  }, [filteredData, allKeywordData, localJob, localYear, groupBy]); // ✨ groupBy를 의존성 배열에 추가

  // ✨ [신규] 필터가 선택되었는지 여부를 확인하는 변수
  const isFilterSelected = localJob !== 'all' || localYear !== 'all';

  return (
      <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
          <div className="flex flex-wrap justify-between items-center mb-4 gap-2">
              <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                  <Icon className="w-5 h-5 mr-2 text-blue-600" />
                  {title}
              </h3>
              <div className="flex items-center gap-2">
                  {/* ✨ [신규] 보기 기준 드롭다운 UI 추가 */}
                  <select value={groupBy} onChange={(e) => setGroupBy(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                      <option value="직무">직무별 보기</option>
                      <option value="직무연차">연차별 보기</option>
                  </select>
                  <select value={localJob} onChange={(e) => setLocalJob(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                      {localJobOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 직무' : option}</option>)}
                  </select>
                  <select value={localYear} onChange={(e) => setLocalYear(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                      {localYearOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 연차' : option}</option>)}
                  </select>
              </div>
          </div>

          {/* ✨ [수정] isFilterSelected 값에 따라 조건부 렌더링 */}
          {isFilterSelected ? (
              Object.keys(groupedKeywords).length > 0 ? (
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                      {Object.entries(groupedKeywords).map(([group, keywords]) => (
                          <div key={group} className="bg-gray-50 rounded-lg p-4">
                              <h4 className="font-medium text-gray-900 mb-3 flex items-center">
                                  <Tag className="w-4 h-4 mr-1 text-gray-600" /> {group}
                              </h4>
                              <div className="flex flex-wrap gap-2">
                                  {keywords.map(({ word, score, count }) => (
                                      <span key={word} title={`점수: ${score.toFixed(2)}, 빈도: ${count}`}
                                          className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium cursor-pointer bg-gray-100 text-gray-700 border border-gray-200">
                                          <Hash className="w-3 h-3 mr-1" /> {word}
                                      </span>
                                  ))}
                              </div>
                          </div>
                      ))}
                  </div>
              ) : (
                  <div className="text-center py-8">
                      <MessageSquare className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                      <p className="text-gray-500">해당 조건의 분석 데이터가 없습니다.</p>
                  </div>
              )
          ) : (
              <div className="text-center py-8 bg-gray-50 rounded-lg">
                  <BarChart3 className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                  <p className="text-gray-500 font-medium">직무 또는 연차를 선택하여 키워드 분석 결과를 확인하세요.</p>
              </div>
          )}
      </div>
  );
};

const TransferAnalysis = ({ filteredData, analysisResult }) => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [localJob, setLocalJob] = useState('all');
  const [localYear, setLocalYear] = useState('all');

  const localJobOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무 || ""))].filter(Boolean), [filteredData]);
  const localYearOptions = useMemo(() => ["all", ...new Set(filteredData.map(item => item.직무연차 || ""))].filter(Boolean), [filteredData]);

  const displayData = useMemo(() => {
    const locallyFilteredData = filteredData.filter(item => {
      const jobMatch = localJob === 'all' || item.직무 === localJob;
      const yearMatch = localYear === 'all' || item.직무연차 === localYear;
      return jobMatch && yearMatch;
    });

    const locallyFilteredIds = new Set(locallyFilteredData.map(item => item.uniqueId));

    const hopefuls = analysisResult.transfer_hopefuls.filter(user =>
      locallyFilteredIds.has(user.uniqueId)
    );

    const total = locallyFilteredData.length;
    const hopeCount = hopefuls.length;

    return {
      hopefuls: hopefuls,
      total: total,
      hopeCount: hopeCount,
      hopePercentage: total > 0 ? Math.round((hopeCount / total) * 100) : 0,
    };
  }, [filteredData, analysisResult, localJob, localYear]);


  const DonutChart = ({ percentage }) => (
    <div className="relative w-24 h-24 flex items-center justify-center">
      <svg className="w-full h-full" viewBox="0 0 36 36" transform="rotate(-90)">
        <path d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
              fill="none" stroke="#e5e7eb" strokeWidth="3" />
        <path d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
              fill="none" stroke="#3b82f6" strokeWidth="3"
              strokeDasharray={`${percentage}, 100`} />
      </svg>
      <div className="absolute text-center transform rotate(90)">
        <span className="block text-xl font-bold text-blue-600">{displayData.hopePercentage}%</span>
        <span className="text-xs text-gray-500">희망</span>
      </div>
    </div>
  );

  return (
    <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
      <div className="flex flex-wrap justify-between items-center mb-4 gap-2">
        <h3 className="text-lg font-semibold text-gray-900">인사이동 희망 여부 분석</h3>
        <div className="flex items-center gap-2">
          <select value={localJob} onChange={(e) => setLocalJob(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
            {localJobOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 직무' : option}</option>)}
          </select>
          <select value={localYear} onChange={(e) => setLocalYear(e.target.value)} className="text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
            {localYearOptions.map(option => <option key={option} value={option}>{option === 'all' ? '전체 연차' : option}</option>)}
          </select>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="flex items-center justify-center space-x-4 bg-gray-50 p-4 rounded-lg">
          <div className="flex flex-col items-center">
            <UserCheck className="w-7 h-7 text-blue-500 mb-1" />
            <span className="font-bold text-lg text-blue-600">{displayData.hopeCount}</span>
            <span className="text-xs text-gray-500">희망자</span>
          </div>
          <DonutChart percentage={displayData.hopePercentage} />
          <div className="flex flex-col items-center">
            <User className="w-7 h-7 text-gray-400 mb-1" />
            <span className="font-bold text-lg text-gray-600">{displayData.total - displayData.hopeCount}</span>
            <span className="text-xs text-gray-500">기타</span>
          </div>
        </div>

        <div className="md:col-span-2">
          <h4 className="font-medium text-gray-800 mb-3">인사이동 희망자 목록 ({displayData.hopeCount}명)</h4>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 max-h-48 overflow-y-auto pr-2">
            {displayData.hopefuls.length > 0 ? (
              displayData.hopefuls.map(user => (
                <div key={user.uniqueId}
                     onClick={() => setSelectedUser(user)}
                     className="flex items-center p-3 rounded-lg border border-gray-200 bg-white hover:bg-blue-50 hover:border-blue-300 cursor-pointer transition-all shadow-sm">
                  <div className="flex-shrink-0 mr-3">
                    <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center">
                      <User className="w-5 h-5 text-blue-600" />
                    </div>
                  </div>
                  <div className="flex-grow min-w-0">
                    <p className="font-semibold text-gray-800 truncate">{user.이름}</p>
                    <p className="text-sm text-gray-500 truncate">{user.직무} / {user.직무연차}</p>
                  </div>
                </div>
              ))
            ) : (
              <div className="flex items-center justify-center h-full">
                <p className="text-center text-gray-500 py-4">해당 조건의 인사이동 희망자가 없습니다.</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {selectedUser && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4" onClick={() => setSelectedUser(null)}>
          <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-lg" onClick={e => e.stopPropagation()}>
            <div className="flex justify-between items-start">
              <div>
                <h3 className="text-xl font-bold text-gray-900">{selectedUser.이름}</h3>
                <p className="text-sm text-gray-600 mt-1">{selectedUser.직무} / {selectedUser.직무연차}</p>
              </div>
              <button onClick={() => setSelectedUser(null)} className="p-1 rounded-full hover:bg-gray-200">
                <X className="w-5 h-5 text-gray-500" />
              </button>
            </div>
            <div className="mt-4 pt-4 border-t">
              <p className="text-sm font-semibold text-gray-700 mb-2">(2) 성장/역량/커리어-구성원 의견</p>
              <div className="max-h-60 overflow-y-auto bg-gray-50 p-3 rounded-md">
                <p className="text-base text-gray-800 whitespace-pre-wrap">
                  {selectedUser['(2) 성장/역량/커리어-구성원 의견']}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const ChartSection = ({ 
    filteredData, 
    transferAnalysisData, 
    workKeywords, 
    growthKeywords, 
    envKeywords 
}) => {
    const jobStats = filteredData.reduce((acc, item) => {
      const job = item.직무 || "기타";
      acc[job] = (acc[job] || 0) + 1;
      return acc;
    }, {});
    const yearStats = filteredData.reduce((acc, item) => {
      const year = item.직무연차 || "미정";
      acc[year] = (acc[year] || 0) + 1;
      return acc;
    }, {});
    const StatCard = ({ title, value, icon: Icon }) => (
      <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-600">{title}</p>
            <p className="text-2xl font-semibold text-gray-900 mt-1">{value}</p>
          </div>
          <div className="p-2 bg-blue-50 rounded-lg"><Icon className="w-5 h-5 text-blue-600" /></div>
        </div>
      </div>
    );

    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <StatCard title="총 면담자 수" value={filteredData.length.toLocaleString()} icon={Users} />
          <StatCard title="직무 유형" value={Object.keys(jobStats).length} icon={BarChart3} />
          <StatCard title="연차 범위" value={Object.keys(yearStats).length} icon={Clock} />
        </div>

        {transferAnalysisData && (
          <TransferAnalysis
            filteredData={filteredData}
            analysisResult={transferAnalysisData}
          />
        )}

        {workKeywords && 
            <KeywordDashboard 
                title="업무 관련 키워드 분석" 
                icon={Briefcase}
                filteredData={filteredData} 
                allKeywordData={workKeywords} 
            />
        }
        {growthKeywords && 
            <KeywordDashboard 
                title="성장/역량 관련 키워드 분석" 
                icon={TrendingUp}
                filteredData={filteredData} 
                allKeywordData={growthKeywords} 
            />
        }
        {envKeywords && 
            <KeywordDashboard 
                title="업무환경 관련 키워드 분석" 
                icon={Home}
                filteredData={filteredData} 
                allKeywordData={envKeywords} 
            />
        }
      </div>
    );
};

const App = () => {
  const [data, setData] = useState([]);
  const [transferAnalysisData, setTransferAnalysisData] = useState(null);

  // ✨ [신규] 3개 의견에 대한 키워드 데이터를 저장할 상태
  const [workKeywords, setWorkKeywords] = useState(null);
  const [growthKeywords, setGrowthKeywords] = useState(null);
  const [envKeywords, setEnvKeywords] = useState(null);

  const [loading, setLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState("");
  const [analysisMode, setAnalysisMode] = useState('pos');
  const [isAnalysisEnabled, setIsAnalysisEnabled] = useState(true);

  const [selectedJob, setSelectedJob] = useState("all");
  const [selectedYear, setSelectedYear] = useState("all");

  const jobOptions = useMemo(() => ["all", ...new Set(data.map((item) => item.직무 || ""))].filter(Boolean), [data]);
  const yearOptions = useMemo(() => ["all", ...new Set(data.map((item) => item.직무연차 || ""))].filter(Boolean), [data]);

  const filteredData = useMemo(() => data.filter((item) => {
    const jobMatch = selectedJob === "all" || item.직무 === selectedJob;
    const yearMatch = selectedYear === "all" || item.직무연차 === selectedYear;
    return jobMatch && yearMatch;
  }), [data, selectedJob, selectedYear]);

  const handleFileUpload = (file) => {
    setLoading(true);
    setLoadingMessage("Excel 파일을 파싱 중입니다...");

    const reader = new FileReader();
    reader.onload = async (e) => {
        try {
            const bytes = e.target.result;
            const workbook = XLSX.read(bytes, { type: "binary" });
            const sheet = workbook.Sheets[workbook.SheetNames[0]];
            let jsonData = XLSX.utils.sheet_to_json(sheet);
            jsonData = jsonData.map((row, index) => ({ ...row, uniqueId: `row_${index}` }));

            setLoadingMessage("AI 모델로 데이터 분석 중입니다...");

            const analysisPromises = [];

            if (isAnalysisEnabled) {
                analysisPromises.push(runAnalysisForRowByRow(jsonData, '(1) 업무-구성원 의견', 'pos'));
                analysisPromises.push(runAnalysisForRowByRow(jsonData, '(2) 성장/역량/커리어-구성원 의견', 'pos'));
                analysisPromises.push(runAnalysisForRowByRow(jsonData, '(3) 업무환경조성-구성원 의견', 'pos'));
            }

            analysisPromises.push(
                fetch('http://127.0.0.1:8000/analyze-transfer-intent', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ data: jsonData }),
                }).then(res => {
                    if (!res.ok) throw new Error('인사이동 분석 API 실패');
                    return res.json();
                })
            );

            const results = await Promise.all(analysisPromises);

            if (isAnalysisEnabled) {
                setWorkKeywords(results[0]);
                setGrowthKeywords(results[1]);
                setEnvKeywords(results[2]);
                setTransferAnalysisData(results[3]);
            } else {
                setWorkKeywords(null);
                setGrowthKeywords(null);
                setEnvKeywords(null);
                setTransferAnalysisData(results[0]);
            }

            setData(jsonData);

        } catch (error) {
            console.error("파일 처리 에러:", error);
            alert(`파일 처리 중 오류가 발생했습니다: ${error.message}`);
        } finally {
            setLoading(false);
            setLoadingMessage("");
        }
    };
    reader.readAsBinaryString(file);
  };

  const downloadCSV = () => {
    const csv = Papa.unparse(filteredData);
    const blob = new Blob([`\uFEFF${csv}`], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "filtered_interview_data.csv";
    link.click();
    URL.revokeObjectURL(url);
  };

  const SkLogo = () => (
    <img src="/logo.svg" alt="Logo" className="w-16 h-16" />
  );

  const ToggleSwitch = ({ isChecked, onChange, children }) => (
    <div className="flex items-center justify-center space-x-2 cursor-pointer" onClick={() => onChange(!isChecked)}>
      {children}
      <div className={`relative inline-flex items-center h-6 rounded-full w-11 transition-colors duration-300 ${isChecked ? 'bg-blue-600' : 'bg-gray-200'}`}>
        <span className={`inline-block w-4 h-4 transform bg-white rounded-full transition-transform duration-300 ${isChecked ? 'translate-x-6' : 'translate-x-1'}`} />
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-3">
                <div className="w-16 h-16 flex items-center justify-center"><SkLogo /></div>
                <div>
                  <h1 className="text-xl font-semibold text-gray-900">면담결과 대시보드</h1>
                </div>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-600"><Bell className="w-5 h-5" /></button>
              <button className="p-2 text-gray-400 hover:text-gray-600"><Settings className="w-5 h-5" /></button>
              <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center"><User className="w-4 h-4 text-gray-600" /></div>
            </div>
          </div>
        </div>
      </nav>
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {data.length === 0 ? (
          <div className="max-w-lg mx-auto">
            <FileUploader onFileUpload={handleFileUpload} />
            <div className="mt-6 flex flex-col items-center space-y-4">
              <ToggleSwitch isChecked={isAnalysisEnabled} onChange={setIsAnalysisEnabled}>
                <Zap className={`w-5 h-5 transition-colors duration-200 ${isAnalysisEnabled ? 'text-blue-600' : 'text-gray-400'}`} />
                <span className={`text-sm font-medium ${isAnalysisEnabled ? 'text-gray-800' : 'text-gray-500'}`}>키워드 분석</span>
              </ToggleSwitch>
              {isAnalysisEnabled && (
                <div className="flex items-center space-x-2">
                  <BrainCircuit className="w-5 h-5 text-gray-500" />
                  <select
                    value={analysisMode}
                    onChange={(e) => setAnalysisMode(e.target.value)}
                    className="block text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                  >
                    <option value="pos">빈도 기반 분석 (추천)</option>
                    <option value="llm">LLM 분석 (고품질)</option>
                    <option value="textrank">TextRank 분석 (빠름 및 영어에 강함)</option>

                  </select>
                </div>
              )}
            </div>
            {loading && (
                <div className="mt-6 flex flex-col items-center">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                    <p className="mt-3 text-sm text-gray-600">{loadingMessage}</p>
                </div>
            )}
          </div>
        ) : (
          <>
            <div className="mb-6 bg-white rounded-lg p-4 shadow-sm border border-gray-200">
                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
                    <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-4">
                    <div className="min-w-0 flex-1 sm:max-w-xs">
                        <label className="block text-xs font-medium text-gray-700 mb-1">직무</label>
                        <select value={selectedJob} onChange={(e) => setSelectedJob(e.target.value)} className="block w-full text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                        {jobOptions.map((option) => <option key={option} value={option}>{option === "all" ? "전체 직무" : option}</option>)}
                        </select>
                    </div>
                    <div className="min-w-0 flex-1 sm:max-w-xs">
                        <label className="block text-xs font-medium text-gray-700 mb-1">연차</label>
                        <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)} className="block w-full text-sm border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500">
                        {yearOptions.map((option) => <option key={option} value={option}>{option === "all" ? "전체 연차" : option}</option>)}
                        </select>
                    </div>
                    </div>
                    {filteredData.length > 0 && (
                    <button onClick={downloadCSV} className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200">
                        <Download className="w-4 h-4 mr-2" /> CSV 다운로드
                    </button>
                    )}
                </div>
            </div>
            {filteredData.length === 0 ? (
                <div className="bg-white rounded-lg p-12 text-center shadow-sm border border-gray-200">
                    <BarChart3 className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">데이터가 없습니다</h3>
                    <p className="text-gray-500">선택된 필터 조건에 맞는 데이터가 없습니다.</p>
                </div>
            ) : (
                <ChartSection 
                  filteredData={filteredData} 
                  transferAnalysisData={transferAnalysisData}
                  workKeywords={workKeywords}
                  growthKeywords={growthKeywords}
                  envKeywords={envKeywords}
                />
            )}
          </>
        )}
      </main>
    </div>
  );
};

export default App;
